import React, {Component} from 'react';
import { Markup, Editor, Container, Column, Row, RuleInput, StyleInput, 
        Document, Button} from './styled'


class App extends Component {

  state = {
    editor: "",
    name0: "",
    begin0: "",
    end0: "",
    style0: "",
    rules: 1
  }

  handleChange = (event) => {
      let {name, value} = event.target
      this.setState({
        [name]: value
      })
  }

  rules = () => {

  }

  newFields = () => {
    this.setState( (prevState) => {
      let {rules} = prevState

      let fields = ['name', 'begin', 'end', 'style']
      let inputValues = {}
      fields.forEach( (field) => {
        inputValues = {
          ...inputValues,
          [`${field}${rules}`]: ''
        }
      });
      rules++
      console.log({
        rules,
        ...inputValues
      })
      return{
        rules,
        ...inputValues
      }
    })
  }

  render(){
    let {value} = this.state
    //object destructuring
    let {handleChange, newFields, rules} = this
  return (
    <Container>
      <Column>
      {rules()}
        <Button onClick={newFields}>
         New Rule
        </Button>
      </Column>
      <Column>
        <Button>
          Random Text
        </Button>
        <Document>
          <Editor
            name={"Editor"}
            value={this.state.value}
            onChange={handleChange}
          />
          <Markup/>
        </Document>
      </Column>
    </Container>
  );
}
}

export default App;
