import React, {Component} from 'react'
import { Markup, Editor, Container, Column, Row, RuleInput, RuleLabel, StyleInput, 
        Document, Button} from './styled'
import hljs from 'highlight.js'
import { rando } from './utils'

console.log(rando)

class App extends Component {

  state = {
    editor: "",
    name0: "",
    begin0: "",
    end0: "",
    style0: "",
    rules: 1
  }

  handleChange = (event) => {
      let {name, value} = event.target
      this.setState({
        [name]: value
      })
  }

  rules = () => {
    let { rules } = this.state
    let array = []
    let fields = ['name', 'begin', 'end']

    for(let i = 0; i < rules; i = i + 1){

      array.push(
        <Row
        key={i}
        >
          <Column>
            {fields.map((field,index) => {
              return (
                <Column
                  key={index}
                >
                  <RuleLabel>{field}</RuleLabel>
                  <RuleInput
                    value={this.state[`${field}${i}`]}
                    onChange={this.handleChange}
                    name={`${field}${i}`}
                  />
                </Column>
            )
            })}
          </Column>
            <StyleInput
              value={this.state[`style${i}`]}
              onChange={this.handleChange}
              name={`style${i}`}
            />
        </Row>
      )
      }
      return array
  }

  newFields = () => {
    this.setState( (prevState) => {
      let {rules} = prevState

      let fields = ['name', 'begin', 'end', 'style']
      let inputValues = {}
      fields.forEach( (field) => {
        inputValues = {
          ...inputValues,
          [`${field}${rules}`]: ''
        }
      });
      rules++
      console.log({
        rules,
        ...inputValues
      })
      return{
        rules,
        ...inputValues
      }
    })
  }

  convertToMarkup = (text = "") => {
    return {
      __html: hljs.highlightAuto(text).value
    }
  }

  language = (newRules) => {
    return () => ({
        contains: [
          ...newRules
        ]
    })
  }

  registerLanguage = (state) => {
    let {rules} = state
    let ruleObjects = []

    for (let i = 0; i < rules; i = i + 1){
      let newRule = {
        className: state[`name${i}`],
        begin: state[`begin${i}`],
        end: state[`end${i}`]
      }
      let {className, begin, end} = newRule
      console.log(className.length, begin.length, end.length)
      if(className.length > 0 && begin.length > 0 && end.length > 0) {
        begin = new RegExp(begin)
        end = new RegExp(end)
        ruleObjects.push(newRule)
        console.log('success')
      }

    }

    hljs.registerLanguage('language', this.language(ruleObjects))
    hljs.configure({
      languages:['language']
    })
  }

  componentWillUpdate(nextProps, nextState){
    this.registerLanguage(nextState)
    console.log(nextState)
  }

  prepareStyles = () => {
    let {rules} = this.state
    let styles = []
    for(let i = 0; i < rules; i = i + 1){
      styles.push(`
        .hljs-${this.state['name' + i]}{
          ${this.state['style' + i]}
        }
      `)
    }

    let newStyles = "".concat(styles).replace(',', "")
    console.log(newStyles)
    return newStyles
  }

  render(){
    let {editor} = this.state
    //object destructuring
    let {handleChange, newFields, rules, convertToMarkup, prepareStyles} = this
  return (
    <Container>
      <Column>
      {rules()}
        <Button onClick={newFields}>
         New Rule
        </Button>
      </Column>
      <Column>
        <Button>
          Random Text
        </Button>
        <Document>
          <Editor
            name={"editor"}
            value={editor}
            onChange={handleChange}
          />
          <Markup
            customStyles={prepareStyles()}
            dangerouslySetInnerHTML={convertToMarkup(editor)}
          />
        </Document>
      </Column>
    </Container>
  );
}
}

export default App;
